import React, { useState, useEffect } from 'react';
import axios from 'axios';
//import './App.css';

function App() {
  // Fixes the 'peers is not defined' error
  const [peers, setPeers] = useState([]);
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const API = "http://localhost:8080";

  const fetchData = async () => {
    try {
      const pRes = await axios.get(`${API}/peers`);
      setPeers(pRes.data);
      const fRes = await axios.get(`${API}/my-files`);
      setFiles(fRes.data);
    } catch (err) { 
      console.error("Backend not running or CORS error"); 
    }
  };

  useEffect(() => { 
    fetchData(); 
  }, []);

  const handleUpload = async () => {
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append("file", selectedFile);
    await axios.post(`${API}/upload`, formData);
    fetchData();
    alert("File shared successfully!");
  };

  return (
    <div className="container">
      <h1>üéì Student Resource P2P</h1>
      
      <div className="card">
        <h3>üì§ Share Your Resources</h3>
        <input type="file" onChange={(e) => setSelectedFile(e.target.files[0])} />
        <button onClick={handleUpload}>Share File</button>
        
        <h4>Your Local Files:</h4>
        <ul>
          {files.map(f => <li key={f}><span>üìÑ {f}</span></li>)}
        </ul>
      </div>

      <div className="card">
        <h3>üåê Active Peers in Hostel</h3>
        <button onClick={fetchData}>Scan Network</button>
        <ul>
          {peers.map(ip => (
            <li key={ip}>
              <span>üìç Peer Student</span>
              <span className="peer-ip">{ip}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

// Fixes the 'export default not found' error
export default App;
