import React, { useState } from "react";

function App() {
  const [peerAddress, setPeerAddress] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);
  const [files, setFiles] = useState([]);

  // Upload file to your own peer server
  const uploadFile = async () => {
    if (!selectedFile) {
      alert("Select a file first!");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    await fetch("http://localhost:5000/upload", {
      method: "POST",
      body: formData
    });

    alert("File shared successfully âœ…");
  };

  // Fetch file list from another peer
  const fetchPeerFiles = async () => {
    try {
      const res = await fetch(`http://${peerAddress}/files`);
      const data = await res.json();
      setFiles(data);
    } catch {
      alert("Peer not reachable âŒ");
    }
  };

  // Download file from peer
  const downloadFile = (filename) => {
    window.location.href = `http://${peerAddress}/download/${filename}`;
  };

  return (
    <div className="container">
      <h1>ðŸ“š Student Resource Sharing (P2P)</h1>

      <div className="card">
        <h2>Upload Resource</h2>
        <input type="file" onChange={e => setSelectedFile(e.target.files[0])} />
        <button onClick={uploadFile}>Upload</button>
      </div>

      <div className="card">
        <h2>Connect to Peer</h2>
        <input
          type="text"
          placeholder="Peer IP:PORT (e.g. 192.168.1.5:5000)"
          value={peerAddress}
          onChange={e => setPeerAddress(e.target.value)}
        />
        <button onClick={fetchPeerFiles}>Discover Files</button>
      </div>

      <div className="card">
        <h2>Available Files</h2>
        {files.length === 0 && <p>No files found</p>}
        <ul>
          {files.map((file, i) => (
            <li key={i}>
              {file}
              <button onClick={() => downloadFile(file)}>Download</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
