import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [peers, setPeers] = useState([]);
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const API = "http://localhost:8080";

  const fetchData = async () => {
    try {
      const pRes = await axios.get(`${API}/peers`);
      setPeers(pRes.data);
      const fRes = await axios.get(`${API}/my-files`);
      setFiles(fRes.data);
    } catch (err) { console.error("Backend not running?"); }
  };

  useEffect(() => { fetchData(); }, []);

  const handleUpload = async () => {
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append("file", selectedFile);
    await axios.post(`${API}/upload`, formData);
    fetchData();
    alert("File shared successfully!");
  };

  return (
    <div style={{ padding: '40px', backgroundColor: '#f4f7f6', minHeight: '100vh' }}>
      <h1>üìÅ Student P2P Resource Sharing</h1>
      <div style={{ background: 'white', padding: '20px', borderRadius: '8px', marginBottom: '20px' }}>
        <h3>Step 1: Share a File (PDF/Notes)</h3>
        <input type="file" onChange={(e) => setSelectedFile(e.target.files[0])} />
        <button onClick={handleUpload}>Upload to My Node</button>
        <h4>My Shared Files:</h4>
        <ul>{files.map(f => <li key={f}>{f}</li>)}</ul>
      </div>

      <div style={{ background: 'white', padding: '20px', borderRadius: '8px' }}>
        <h3>Step 2: Discovered Peers on Network</h3>
        <button onClick={fetchData}>Refresh Network</button>
        <ul>
          {peers.map(ip => (
            <li key={ip}>üìç Peer Student at: <strong>{ip}</strong></li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
